  "base_id": "base_ckd_dosing_0044",
  "recommendations": {
    "gabapentin": {
      "note": "In CKD stage 3b (eGFR 30-44), gabapentin dose adjustment is recommended due to reduced renal clearance.",
      "dose_adjustment": "Reduce dose frequency to BID or adjust total daily dose accordingly.",
      "monitoring": "Monitor for signs of gabapentin toxicity such as dizziness or sedation."
    },
    "metformin": {
      "note": "Metformin use is generally considered safe with eGFR ≥30 mL/min/1.73m² but caution is advised.",
      "dose_adjustment": "Continue metformin at current dose with close monitoring of renal function. Avoid initiating if eGFR falls below 45; discontinue if eGFR falls below 30.",
      "monitoring": "Monitor renal function closely to prevent lactic acidosis."
    },
    "additional_management": {
      "note": "BP is slightly elevated; consider optimizing antihypertensive therapy to protect kidney function.",
      "allergies": "Avoid penicillin and related antibiotics.",
      "diabetes_management": "Continue glycemic control with attention to renal dosing adjustments."
    }
  },
  "disclaimer": "FOR EVALUATION ONLY - NOT CLINICAL USE."
}
