  "recommendations": [
    {
      "medication": "gabapentin",
      "recommendation": "Reduce dose due to CKD stage 3b. Suggested dose: 300 mg once daily.",
      "rationale": "Gabapentin is renally cleared. In patients with eGFR 30-59, dose reduction is recommended to avoid toxicity."
    },
    {
      "medication": "metformin",
      "recommendation": "Use with caution. Continue at current dose with monitoring.",
      "rationale": "Metformin is generally contraindicated if eGFR <30. With eGFR 32, therapy can continue but monitor renal function closely to avoid lactic acidosis."
    }
  ],
  "monitoring": [
    "Monitor renal function (eGFR, serum creatinine) regularly.",
    "Monitor for signs of gabapentin toxicity (e.g., dizziness, somnolence).",
    "Monitor blood glucose control due to diabetes."
  ],
  "follow_up": "Reassess medication regimen if eGFR declines further below 30 mL/min/1.73 m2."
}
