{
  "base_id": "base_ckd_dosing_0114",
  "recommendations": {
    "gabapentin": {
      "current_dose": "300mg TID",
      "recommendation": "Dose adjustment recommended due to CKD stage 3b (eGFR 32). Consider dose reduction or increased dosing interval to avoid accumulation and toxicity. Typical dosing in CKD stage 3b is usually reduced by 50%. Consult latest guidelines.",
      "suggested_dose": "150mg TID or 300mg BID"
    },
    "metformin": {
      "current_dose": "500mg BID",
      "recommendation": "Use with caution due to eGFR 32 mL/min. Metformin is not contraindicated but requires dose adjustment and monitoring. FDA recommends caution if eGFR is between 30-45 mL/min; consider dose reduction and monitor renal function closely.",
      "suggested_dose": "Consider maintaining current dose with caution and regular renal function monitoring; some clinicians reduce dose or frequency."
    }
  },
  "monitoring": [
    "Monitor renal function (eGFR, serum creatinine) frequently at least every 3 months or as clinically indicated.",
    "Monitor for signs of gabapentin toxicity (dizziness, somnolence).",
    "Monitor blood glucose control considering diabetes and metformin use."
  ],
  "disclaimer": "FOR EVALUATION ONLY - NOT CLINICAL USE."
}
