{
  "base_id": "base_ckd_dosing_0024",
  "ckd_stage": "3b",
  "recommendations": {
    "gabapentin": {
      "dose_adjustment": "reduce dose",
      "rationale": "Gabapentin is renally cleared; in CKD stage 3b (eGFR 30-44), dose adjustment needed to avoid accumulation and toxicity.",
      "suggested_dose": "300mg once daily or based on clinical response",
      "monitoring": "monitor for signs of toxicity including dizziness, somnolence"
    },
    "metformin": {
      "dose_adjustment": "use with caution",
      "rationale": "Metformin is contraindicated if eGFR <30 mL/min; at eGFR 32 mL/min, benefits may outweigh risks with careful monitoring for lactic acidosis.",
      "suggested_dose": "continue 500mg BID with renal function monitoring",
      "monitoring": "monitor renal function closely every 3-6 months; watch for signs of lactic acidosis"
    }
  },
  "additional_notes": "Blood pressure is slightly elevated; manage hypertension according to CKD guidelines to slow progression. Continue regular monitoring of renal function and glycemic control."
}
